import React from 'react';
import {useForm} from "react-hook-form";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import classes from "./CustomForm.module.scss";
const CustomForm = () => {
    const {
        register,
        formState:{
            errors,
            isValid
        },
        handleSubmit,
        reset,
    } = useForm({mode:'onChange'})

    const submitHandler = (data) =>{
        alert(JSON.stringify(data))
        reset()
    }
    console.log(errors?.password)
    return (
        <Form onSubmit={handleSubmit(submitHandler)} className={classes.formCl}>
            <Form.Group className="mb-3">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                    isInvalid={errors?.firstName}
                    type='text'
                    placeholder="name@example.com"
                    {...register('firstName', {
                        required:"Поле обязательно для заполнения",
                        pattern: {
                            value: /^[A-Za-z]+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}/,
                            message: 'Не валидный email' // JS only: <p>error message</p> TS only support string
                        }
                })}/>
                {errors?.firstName
                    &&
                    <Form.Text className="text-muted">
                        {errors?.firstName?.message || 'Error'}
                    </Form.Text>}
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control isInvalid={errors?.password} type="password" placeholder="Password" {...register('password',{
                    minLength: {
                        value:6,
                        message: 'Минимальная длина - 6 символов'
                    },
                    maxLength: {
                        value:35,
                        message:'Максимальная длина - 35 символов'
                    },
                    required:"Поле обязательно для заполнения",
                })}/>
                {errors?.password
                    &&
                    <Form.Text className="text-muted">
                        {errors?.password?.message || 'Минимальная длина - 6 символов'}
                    </Form.Text>}
            </Form.Group>


            <Button disabled={!isValid} variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    );
};

export default CustomForm;
